Application.ScrollMagic.Scenes.Portfolio=function(){const TIMELINE=new TimelineMax;for(const[index,element]of Object.entries(document.querySelectorAll('[data-target="portfolioRowPicture"]')))TIMELINE.fromTo(element,50+15*index,{scale:.95,opacity:0,y:10,rotationY:10},{scale:1,opacity:1,y:0,rotationY:0},4*index);for(const[index,element]of Object.entries(document.querySelectorAll('[data-target="portfolioRow"]')))if(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent))$(element).on("swipeleft",(function(){TweenMax.to(element,1,{x:"-=250"})})),$(element).on("swiperight",(function(){TweenMax.to(element,1,{x:"+=250"})}));else{const LENGTH=$(element).find("img").length,WIDTH=$(element).find("img").width(),DIVIDER=.1;index%2==0?TIMELINE.fromTo(element,500,{x:WIDTH*LENGTH*DIVIDER},{x:WIDTH*LENGTH*-1*DIVIDER},0):TIMELINE.fromTo(element,500,{x:WIDTH*LENGTH*-1*DIVIDER},{x:WIDTH*LENGTH*DIVIDER},0)}return Application.Layout.set("Portfolio",new ScrollMagic.Scene({triggerElement:"#scene-portfolio",triggerHook:.4,duration:"100%"}).setTween(TIMELINE).addTo(Application.ScrollMagic.Controllers.Main)),!0};